CREATE PROCEDURE [dbo].[CodeDeploy_UPDATE]
(      
	@ENV NVARCHAR(100),
	@Application NVARCHAR(100),	
	@CodeDeployName NVARCHAR(100),
	@CodePipelineName NVARCHAR(100),
	@S3 NVARCHAR(100),
	@App_Spec NVARCHAR(MAX),
	@IsActive NVARCHAR(100)
)
AS

BEGIN
	SET NOCOUNT ON;  
	SET @HeaderText = 'Modify Urls Details'
	--SELECT * FROM CONSUMERS
	SELECT	
		@Application = A.ApplicationName,
		@Server = SM.ServerName,
		@HCCBAPP2 = HCCBAPP2,
		@HCCBAPP3 = HCCBAPP3,
		@HCCBAPP4 = HCCBAPP4,
		@HCCBAPP5 = HCCBAPP5,
		@HCCBAPP6 = HCCBAPP6,
		@SAASAPP4 = SAASAPP4,
		@SAASAPP5 = SAASAPP5,
		@SAASAPP6 = SAASAPP6,
		@HCCBFAT = HCCBFAT,
		@HCCBUAT = HCCBUAT,
		@SAASSIT = SAASSIT,
		@SAASUAT = SAASUAT,
		@SITV2APP = SITV2APP,
		@UATV2APP = UATV2APP,
		@PRDV2APP1 = PRDV2APP1,
		@SAASPILOT = SAASPILOT,
		@SAASDEMO = SAASDEMO,
		@DEMOV2 = DEMOV2,
		@TRAINING = TRAINING,
		@IsActive = CASE C.IsActive WHEN 1 THEN 'Active' ELSE 'InActive' END
	FROM Consumers C
	INNER JOIN ServerMaster SM WITH(NOLOCK) ON C.ServerID = sm.ID
	INNER JOIN Applications A WITH(NOLOCK) ON C.ApplicationID = A.ID
	WHERE SM.ServerName = @Server
	AND A.ApplicationName = @Application
END

