CREATE PROCEDURE [dbo].[RolePermission_Select]
(
@RoleID int,
@Module Nvarchar(100),
@ActionSave Nvarchar(100),
@ActionAdd Nvarchar(100),
@ActionEdit Nvarchar(100),
@ActionDelete Nvarchar(100),
@Save Nvarchar(100) OUTPUT,
@Add Nvarchar(100) OUTPUT,
@Edit Nvarchar(100) OUTPUT,
@Delete Nvarchar(100) OUTPUT
)

AS

BEGIN
	SET NOCOUNT ON;  
	SET @HeaderText  = 'Modify User Details'

	--SELECT * FROM HTSMaster

SELECT 
	@USERID = UserId,
	@Name = Name,
	@UserName = UserName,
	@Password = Password,
	@Email = Email
FROM Users 
WHERE UserId = @USERID
END

CREATE PROCEDURE [dbo].[UE_Edit]
(      
	@USERID NVARCHAR(100),
	@Name NVARCHAR(100) OUTPUT,	
	@UserName NVARCHAR(100) OUTPUT,
	@Password NVARCHAR(100) OUTPUT,
	@ActiveStatus NVARCHAR(100) OUTPUT,
	@Email NVARCHAR(100) OUTPUT,
	@HeaderText NVARCHAR(100) OUTPUT
	
)
AS

BEGIN
	SET NOCOUNT ON;  
	SET @HeaderText = 'Modify Urls Details'

	SELECT	
		@Application = A.ApplicationName,
		@Client = CM.ClientName,	

		@Url1 =  U.Url1,
		@Url2 = U.Url2,
		@Url3 = U.Url3,
		@IsActive = CASE U.IsActive WHEN 1 THEN 'Active' ELSE 'InActive' END
	FROM URLS U
	INNER JOIN Applications A WITH(NOLOCK) ON U.ApplicationID = A.ID
	INNER JOIN ClientMaster CM WITH(NOLOCK) ON U.ClientId = CM.ID
	WHERE CM.ClientName = @Client
	AND A.ApplicationName = @Application
END

