CREATE PROCEDURE [dbo].[User_LoginValidate]
      @Username NVARCHAR(20),
      @Password NVARCHAR(200),      
      @RoleId NVARCHAR(100) OUTPUT,
      @Class NVARCHAR(100) OUTPUT,
      @Section NVARCHAR(100) OUTPUT,
	  @Medium NVARCHAR(100) OUTPUT,
      @Phonenumber NVARCHAR(10) OUTPUT,
      @OTP NVARCHAR(10) OUTPUT,
	  @Changepassword NVARCHAR(10) OUTPUT,
	  @UserId NVARCHAR(10) OUTPUT
AS

CREATE PROCEDURE [dbo].[User_OtpValidate]
      @Username NVARCHAR(20),
      @Password NVARCHAR(20),
      @OTP INT =null,
      @RoleId NVARCHAR(100) OUTPUT,
      @Class NVARCHAR(100) OUTPUT,
      @Section NVARCHAR(100) OUTPUT
AS

BEGIN
	SET NOCOUNT ON;  
	SET @HeaderText  = 'Modify User Details'

	--SELECT * FROM users

SELECT 
	@USERID = UserId,
	@RoleName = RM.RoleName,
	@Name = Name,
	@UserName = UserName,
	@Password = Password,
	@Email = Email
FROM Users U
LEFT OUTER JOIN RoleMaster RM WITH(NOLOCK) ON U.RoleID = RM.RoleID
LEFT OUTER JOIN ActiveStatus ACS WITH(NOLOCK) ON U.Status = ACS.Id
WHERE UserId = @USERID
END

